import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, Image, TouchableOpacity, TextInput } from 'react-native'
import MapView, { Marker } from 'react-native-maps'
import { getLandmarks, getUsers } from './services/api'
import { Landmark } from './models/Landmark'
import { User } from './models/User'

export default function App() {
  const [landmarks, setLandmarks] = useState([] as Landmark[])
  const [users, setUsers] = useState([] as User[])
  const [currentUserIndex, setCurrentUserIndex] = useState(0)

  useEffect(() => {
    var fetchData = async () => {
      setLandmarks(await getLandmarks())
      setUsers(await getUsers())
    }

    if (landmarks.length === 0) {
      fetchData()
    }
  }, []);

  var getUser = (index: number) => {
    if (!users || index < 0 || index >= users.length) {
      return ''
    }

    return users[index].fullname
  }

  var changeUser = () => {
    setCurrentUserIndex((currentUserIndex + 1) % users.length)
  }

  return (
    <>
      <View style={styles.container}>
        <MapView style={styles.map} initialRegion={{
          latitude: -37.807,
          longitude: 144.991,
          latitudeDelta: 0.05,
          longitudeDelta: 0.05,
        }}>
          {landmarks.map((landmark) =>
            <Marker
              key={landmark.id}
              coordinate={{ latitude: landmark.latitude, longitude: landmark.longitude }}
              title={(users.find(u => u.username === landmark.username))?.fullname}
              description={landmark.comment}
            />
          )}
        </MapView>
        <View style={styles.toolbar}>
          <View style={styles.userToolbar}>
            <Text style={styles.user}>{getUser(currentUserIndex)}</Text>
            <TouchableOpacity onPress={() => changeUser()}>
              <Text style={styles.changeUser}> [Change]</Text>
            </TouchableOpacity>
          </View>
          <View style={styles.commentToolbar}>
            <TextInput
              style={styles.comment}
              multiline
              numberOfLines={3}
              maxLength={500}
            />
            <Text style={styles.add}>Add</Text>
          </View>
        </View>
        <Image source={require('./images/crosshair.png')} style={styles.crosshair} />
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  map: {
    flex: 1,
    width: '100%',
    height: '100%',
  },
  crosshair: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    width: 50,
    height: 50,
    marginLeft: -25,
    marginTop: -25,
  },
  toolbar: {
    flex: 0,
    padding: 20,
    width: '100%',
  },
  userToolbar: {
    flexDirection: 'row',
    paddingBottom: 10,
    justifyContent: 'flex-start',
  },
  user: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  changeUser: {
    fontSize: 16,
    color: '#3366ff',
  },
  commentToolbar: {
    flexDirection: 'row',
  },
  comment: {
    borderColor: '#333333',
    borderWidth: 1,
    flex: 1,
  },
  add: {
    flex: 0,
    textAlign: 'right',
    fontSize: 16,
    color: '#3366ff',
  }
})
