import React, { useState } from 'react';
import { StyleSheet, Text, View, Image } from 'react-native';
import MapView, { Marker } from 'react-native-maps';
import { getLandmarks } from './services/api'

export default async function App() {
  const [landmarks, setLandmarks] = useState(await getLandmarks());

  return (
    <>
      <View style={styles.container}>
        <MapView style={styles.map} initialRegion={{
          latitude: -37.8125,
          longitude: 145,
          latitudeDelta: 0.05,
          longitudeDelta: 0.05,
        }}>
          {landmarks.map((landmark) =>
            <Marker
              coordinate={{ latitude: landmark.latitude, longitude: landmark.longitude }}
              title={landmark.username}
              description={landmark.comment}
            />
          )}
        </MapView>
        <View style={styles.toolbar}>
          <Text style={styles.logo}>Landmark Remark {landmarks.length}</Text>
          <Text style={styles.link}>Add</Text>
        </View>
        <Image source={require('./images/crosshair.png')} style={styles.crosshair} />
      </View>
    </>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  map: {
    flex: 1,
    width: '100%',
    height: '100%',
  },
  crosshair: {
    position: 'absolute',
    top: '50%',
    left: '50%',
    width: 50,
    height: 50,
    marginLeft: -25,
    marginTop: -25,
  },
  toolbar: {
    flex: 0,
    padding: 20,
    flexDirection: 'row',
  },
  logo: {
    flex: 1,
    fontSize: 16,
    fontWeight: 'bold',
  },
  link: {
    fontSize: 16,
    color: '#3366ff',
  }
});
